userDefinedLinkHintCss:
/* Import a thin monospace from Google Fonts (paste in a <style> or user CSS that supports @import) */
@import url('https://fonts.googleapis.com/css2?family=Fira+Code:wght@300;400;600&display=swap');

div > .vimiumHintMarker {
  background: rgba(0,0,0,0.9);
  border: 1px solid rgba(255,255,255,0.06);
  padding: 3px 6px;
  border-radius: 4px;
  box-shadow: none;
}

/* thin monospace text */
div > .vimiumHintMarker span {
  font-family: 'Fira Code', monospace;
  font-size: 14px;
  font-weight: 300;               /* low weight -> thinner glyphs */
  letter-spacing: 0.6px;          /* slightly wider spacing feels thinner */
  line-height: 1;
  color: #e6f0ff;
  -webkit-font-smoothing: antialiased;
  text-rendering: optimizeLegibility;
  text-shadow: none;
}
